import React, { useState } from 'react';

const SearchBar = () => {

  const [selectedUser, setSelectedUser] = useState(null);
  const [searchField, setSearchField] = useState('id');
  const [searchValue, setSearchValue] = useState('');

  const handleSearch = async () => {
    try {
      const response =
        await fetch(`http://localhost:8000/api/admin/user/search?field=${searchField}&value=${searchValue}`, {
          method: 'GET',
        });
        if (!response.ok) {
          throw new Error('No user found');
        }
        const userData = await response.json();
        setSelectedUser(userData[0]);
    } catch (err) {
      alert(`Fail to fetch user: ${err}`);
    }
  };

  return (
    <div>
      <select value={searchField} onChange={e => setSearchField(e.target.value)}>
        <option value="id">ID</option>
        <option value="username">Username</option>
        <option value="email">Email</option>
        <option value="phoneNumber">Phone Number</option>
      </select>

      <input type="text" value={searchValue} onChange={e => setSearchValue(e.target.value)} placeholder="Enter search value"/>

      <button onClick={handleSearch}>Search</button>

      {selectedUser && (
        <div>
          {/* Display user information here. This is just an example, adjust according to your user structure. */}
          <p>Username: {selectedUser.username}</p>
          <p>Email: {selectedUser.email}</p>
          <p>Phone Number: {selectedUser.phone}</p>
        </div>
      )}
    </div>
  );
};

export default SearchBar;
