import React, {useState} from 'react';
import { Button, Form, Container } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import './UserLogIn.css'; // Import your CSS

const UserLogin = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [showSignUp, setShowSignUp] = useState(false);
  const [showLogIn, setShowLogIn] = useState(true);

  const handleShowSignUp = () => {
    setShowSignUp(true);
  }

  const handleShowLogIn = () => {
    setShowLogIn(false);
  }

  const handleLogin = (e) => {
    e.preventDefault();

    if (username && password) {
      fetch('http://localhost:8000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      })
        .then(response => response.json().then(data => ({ status: response.status, body: data })))
        .then(data => {
          if (data.status === 200) {
            navigate('/admin');
          } else {
            setUsername('');
            setPassword('');
            alert(data.body.error || "Username or password is incorrect.");
          }
        })
        .catch(() => {
          setUsername('');
          setPassword('');
          alert('Failed to authenticate.');
        });
    } else {
      setUsername('');
      setPassword('');
      alert("At least one field (username or password) is missing.");
    }
  };
  const handleSignUp = (e) => {
    e.preventDefault();

    // Check if all fields are filled
    if (!(username && password && email && phone)) {
      alert("Please fill all the fields.");
      return;
    }

    fetch('http://localhost:8000/api/users', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password, email, phone }),
    })
    .then(response => response.json()
      .then(data => {
        if (!response.ok) {
          // throw error so that it can be caught and handled in the catch block
          throw new Error(data.error);
        }
        return data;
      }))
    .then(data => {
      if (data) {
        navigate('/');
        setUsername('');
        setPassword('');
        setEmail('');
        setPhone('');
      }
    })
    .catch(error => {
      alert(`Sign-up failed: ${error.message}`);
      setUsername('');
      setPassword('');
      setEmail('');
      setPhone('');
    });
  };

  return (
    <Container className="login-container">
      {!showSignUp ?
        <Form onSubmit={handleLogin}>
          <Form.Group controlId="formUsername">
            <Form.Label>Username</Form.Label>
            <Form.Control type="text" placeholder="Enter username" value={username} onChange={e => setUsername(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="formPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
          </Form.Group>

          <Button variant="primary" type="submit">
            Login
          </Button>
          <Button variant="link" onClick={handleShowSignUp}>
            Sign Up
          </Button>
        </Form>
      :
        <Form onSubmit={handleSignUp}>
          <Form.Group controlId="formUsername">
            <Form.Label>Username</Form.Label>
            <Form.Control type="text" placeholder="Enter username" value={username} onChange={e => setUsername(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="formPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="formEmail">
            <Form.Label>Email</Form.Label>
            <Form.Control type="email" placeholder="Enter email" value={email} onChange={e => setEmail(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="formPhone">
            <Form.Label>Phone Number</Form.Label>
            <Form.Control type="text" placeholder="Enter phone number" value={phone} onChange={e => setPhone(e.target.value)} />
          </Form.Group>

          <Button variant="primary" type="submit">
            Sign Up
          </Button>
          <Button variant="link" onClick={handleShowLogIn}>
            Back to Log In
          </Button>
        </Form>
      }
    </Container>
  );
};

export default UserLogin;
