import React, { useState } from 'react';
import UserInfoTable from './UserInfoTable';
import './SearchBar.css';

const SearchBar = () => {

  const [selectedUser, setSelectedUser] = useState(null);
  const [searchField, setSearchField] = useState('id');
  const [searchValue, setSearchValue] = useState('');
  const [loadTable, setLoadTable] = useState(false);

  const handleSearch = async () => {
    try {
      const response =
        await fetch(`http://localhost:8000/api/admin/user/search?field=${searchField}&value=${searchValue}`, {
          method: 'GET',
        });
        console.log(response);
        if (!response.ok) {
          throw new Error('No user found');
        }
        const userData = await response.json();
        if (userData && userData.length > 0) {
            setSelectedUser(userData[0]);
            setLoadTable(true);
        } else {
            alert('No user found');
        }
    } catch (err) {
      alert(`Fail to fetch user: ${err}`);
    }
  };

  const handleSelectChange = (e) => {
    e.preventDefault();
    setSearchField(e.target.value);
    setSearchValue('');
    setLoadTable(false);
  }

  return (
    <div className='SearchBar-container'>
      <div>
        <select value={searchField} onChange={handleSelectChange}>
          <option value="id">ID</option>
          <option value="username">Username</option>
          <option value="email">Email</option>
          <option value="phoneNumber">Phone Number</option>
        </select>

        <input type="text" value={searchValue} onChange={e => setSearchValue(e.target.value)} placeholder="Enter search value"/>

        <button onClick={handleSearch}>Search</button>
      </div>
      {selectedUser && loadTable && (
        <UserInfoTable allUsers={[selectedUser]}/>
      )}
    </div>
  );
};

export default SearchBar;
