import React, { useState } from 'react';
import { Button, Modal, Form } from 'react-bootstrap';
import './AddProduct.css';

const AddProduct = ({ show, handleClose, handleAddItem }) => {
  const [prod_name, setProdName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState(0);
  const [quantity, setQuantity] = useState(0);

  const handleSubmit = () => {
    const newItem = {
      prod_name,
      description,
      price,
      quantity
    };
    handleAddItem(newItem);
    resetDefault();
  };

  const handleCancel = () => {
    handleClose();
    resetDefault();
  };

  const resetDefault = () => {
    setProdName('');
    setDescription('');
    setPrice(0);
    setQuantity(0);
  };

  return (
    <Modal show={show} onHide={handleClose} className="add-product-modal">
      <Modal.Header closeButton>
        <Modal.Title>Add New Item</Modal.Title>
      </Modal.Header>
      <Modal.Body className='modal-body'>
        <Form className="modal-form">
          <Form.Group controlId="prodName" className='form-group'>
            <Form.Label className="form-label">Product Name</Form.Label>
            <Form.Control
              className="form-control"
              type="text"
              value={prod_name}
              onChange={(e) => setProdName(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="description" className='form-group'>
            <Form.Label className="form-label">Description</Form.Label>
            <Form.Control
              className="form-control"
              type="text"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="price">
            <Form.Label className="form-label">Price</Form.Label>
            <Form.Control
              className="form-control"
              type="number"
              value={price}
              onChange={(e) => setPrice(parseFloat(e.target.value))}
            />
          </Form.Group>
          <Form.Group controlId="quantity">
            <Form.Label className="form-label">Quantity</Form.Label>
            <Form.Control
              className="form-control"
              type="number"
              value={quantity}
              onChange={(e) => setQuantity(parseInt(e.target.value))}
            />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer className='modal-footer'>
        <Button variant="secondary" onClick={handleCancel}>
          Cancel
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          Add Item
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddProduct;
