import React, { useContext } from 'react';
import { Button } from 'react-bootstrap';
import { CartContext } from './CartContext';
import './Cart.css';

const Cart = () => {
  const { cart, toggleCartVisibility, isCartVisible } = useContext(CartContext);

  const cartButtonStyle = {
    marginRight: isCartVisible ? '300px' : '15px',  // Adjust the margin based on cart visibility
    transition: 'margin 0.3s ease-in-out',  // Add a smooth transition effect
  };

  return (
    <header className="cart">
      <Button className="cart-button" style={cartButtonStyle} onClick={toggleCartVisibility}>
        Cart ({cart.length})
      </Button>
    </header>
  );
};

export default Cart;
