import React, { useContext } from 'react';
import { CartContext } from './CartContext';
import './CartDisplay.css';

const CartDisplay = () => {
  const { cart, isCartVisible } = useContext(CartContext);

  if (!isCartVisible) {
    return null;
  }

  return (
    <div className="cart">
      <h2>Shopping Cart</h2>
      {cart.map((item, index) => (
        <div key={index} className="cart-item">
          <p>{item.name}</p>
          <p>${item.price}</p>
        </div>
      ))}
    </div>
  );
};

export default CartDisplay;
