import React, { useContext, useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import { useNavigate, useParams } from 'react-router-dom';
import { UserContext } from './UserContext';


const UserHomePage = () => {
  const params = useParams();
  const userId = params.id;

  const { logOut } = useContext(UserContext);
  const navigate = useNavigate();

  const [userInfo, setUserInfo] = useState(null);

  const handleLogOut = () => {
    logOut();
    navigate('/');
    alert(`Logged out for user ${userId}`);
  }

  useEffect(() => {
    // Fetch the history data when the component is mounted
    const fetchHistoryData = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/users`);
        let users = await response.json();
        const targetUser = users.find(user => user._id === userId);
        if (targetUser) {
          setUserInfo(targetUser);
        } else {
          console.error("User not found");
        }
      } catch (error) {
        console.error("Failed to fetch history data:", error);
      }
    };

    fetchHistoryData();
  }, [userId]);


  return (
    <div className='home-page-container'>
      <h2 className='user-info'>Username: {userInfo.username}</h2>
      <h3 className='user-info'>User Id: {userId}</h3>
      <Button className='logout-button' onClick={handleLogOut}>Log out</Button>
    </div>
  );

}

export default UserHomePage;
