import React, { useContext } from 'react';
import { CartContext } from './CartContext';
import './CartDisplay.css';

const CartDisplay = () => {
  const { cart, isCartVisible, toggleCartVisibility, clearCart } = useContext(CartContext);

  if (!isCartVisible) {
    return null;
  }

  const handleClearCart = () => {
    clearCart();
  }

  const handleCloseCart = () => {
    toggleCartVisibility();
  }

  const handleCheckout = () => {
    // Handle the checkout logic
    if (cart.length > 0) {
      const totalPrice = cart.reduce((acc, item) => acc + item.price, 0);
      alert(`Checkout completed. Total price: $${totalPrice}`);
      clearCart();
    } else {
      alert('Your cart is empty.');
    }
  };

  return (
    <div className="cart-display">
      <h2>Shopping Cart</h2>
      {cart.map((item, index) => (
        <div key={index} className="cart-item">
          <p>{item.name}</p>
          <p>${item.price}</p>
        </div>
      ))}
      <div classname='cart-buttons'>
      <button className="clear-cart-button" onClick={handleClearCart}>
          Clear Cart
      </button>
      <button className="close-cart-button" onClick={handleCloseCart}>
          Close Cart
      </button>
      <button className="close-cart-button" onClick={handleCheckout}>
          Check out
      </button>
      </div>
    </div>
  );
};

export default CartDisplay;
