import React, { useState } from 'react';
import { Form, Button, Modal } from 'react-bootstrap';
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';
import './CheckoutBox.css';

const CheckoutBox = ({ handleCheckout }) => {
  const [show, setShow] = useState(false);
  const stripe = useStripe();
  const elements = useElements();

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleSubmit = async (event) => {
    event.preventDefault();
    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: 'card',
      card: elements.getElement(CardElement),
    });

    if (!error) {
      console.log(paymentMethod);
      handleCheckout(); // Call your handleCheckout function here
    }
  };

  return (
    <>
      <Button variant="primary" className="checkout-button" onClick={handleShow}>
        Checkout
      </Button>

      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header closeButton>
          <Modal.Title className="payment-title">Secure Checkout</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="payment-container">
            <Form onSubmit={handleSubmit} className="payment-form">
              <div className="payment-form-row">
                <input type="text" className="payment-form-input" placeholder="First Name" />
                <input type="text" className="payment-form-input" placeholder="Last Name" />
              </div>
              <div className="card-element-wrapper">
                <CardElement />
              </div>
              <Button variant="primary" type="submit" block className="payment-form-button">
                Pay
              </Button>
            </Form>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" className="close-button" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default CheckoutBox;
