import React, { useState, useEffect } from 'react';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Doughnut } from 'react-chartjs-2';
import { useParams } from 'react-router-dom';

function UserHistory() {
  const params = useParams();
  const userId = params.id;

  // State to store the fetched history data
  const [historyData, setHistoryData] = useState(null);

  useEffect(() => {
    // Fetch the history data when the component is mounted
    const fetchHistoryData = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/users`);
        let users = await response.json();
        const targetUser = users.find(user => user._id === userId);
        if (targetUser) {
          setHistoryData(targetUser.history); // update the state with the fetched data
        } else {
          console.error("User not found");
        }
      } catch (error) {
        console.error("Failed to fetch history data:", error);
      }
    };

    fetchHistoryData();
  }, [userId]); // dependency array, only re-run the effect if userId changes


  if (!historyData) {
    return <div>Loading...</div>;
  }

  ChartJS.register(ArcElement, Tooltip, Legend);

  const totalQuantities = historyData
    .flat()
    .reduce((acc, item) => {
      Object.keys(item).forEach((key) => {
        if (!acc[key]) {
          acc[key] = item[key];
        } else {
          acc[key] += item[key];
        }
      });
      return acc;
    }, {});

  const backgroundColor = [
    'rgba(255, 99, 132, 0.5)',
    'rgba(53, 162, 235, 0.5)',
    'rgba(181, 2, 160, 0.5)',
    'rgba(227, 223, 0, 0.5)',
    'rgba(34, 139, 34, 0.5)',
  ]
  const borderColor = [
    'rgba(255, 99, 132)',
    'rgba(53, 162, 235)',
    'rgba(181, 2, 160)',
    'rgba(227, 223, 0)',
    'rgba(34, 139, 34)',
  ]

  const chartData = {
    labels: Object.keys(totalQuantities),
    datasets: [
      {
        label: '# of purchases',
        data: Object.values(totalQuantities),
        backgroundColor: backgroundColor,
        borderColor: borderColor,
        borderWidth: 1,
      },
    ],
  };

  // Display the history data
  return (
    <div>
      <h1>User ID: {userId}</h1>
      <h2>Total Quantities:</h2>
      <pre>{JSON.stringify(totalQuantities, null, 2)}</pre>
      <Doughnut data={chartData} />
    </div>
  );
}

export default UserHistory;
