import React, { useContext } from 'react';
import { Button, Container, Row, Col } from 'react-bootstrap';
import { CartContext } from './CartContext';
import './CartDisplay.css';

const CartDisplay = () => {
  const { cart, updateCart, emptyCart } = useContext(CartContext);

  const calculateTotal = () => {
    return cart.reduce((sum, product) => sum + product.quantity * product.price, 0);
  };

  const updateQuantity = (product, quantity) => {
    updateCart(product, quantity);
  }

  return (
    <Container fluid className="cart-display">
      <h2>Your Cart</h2>
      {cart.map((product) => (
        <Row key={product._id} className="cart-item">
          <Col xs={5} className="cart-description">{product.prod_name}</Col>
          <Col xs={3}>${product.price}</Col>
          <Col xs={2}>x
            <input
              type="number"
              id="quantity-select"
              value={product.quantity}
              min={1}
              max={product.max_quantity}
              onChange={(e) => updateQuantity(product, parseInt(e.target.value))}
            />
          </Col>
          <Col xs={2}>
            <Button onClick={() => updateQuantity(product, 0)}>Remove</Button>
          </Col>
        </Row>
      ))}
      <Row className="total-price">
        <Col>Total:</Col>
        <Col>${calculateTotal()}</Col>
      </Row>
      <Row className="cart-buttons">
        <Button className="checkout-button">Checkout</Button>
        <Button className="clear-cart-button" onClick={emptyCart}>Clear</Button>
      </Row>
    </Container>
  );
};

export default CartDisplay;
