import React, { useContext } from 'react';
import { Button } from 'react-bootstrap';
import { CartContent } from './CartContent';
import './Cart.css';

const Cart = () => {
  const { inCart } = useContext(CartContent);

  return (
    <header>
      <Button className="cart-button">Cart ({inCart.length})</Button>
    </header>
  );
};


export default Cart;
