import React, { useEffect, useState } from 'react';
import { Chart as ChartJS, BarElement, CategoryScale, LinearScale, Title, Tooltip } from 'chart.js';
import { Bar } from 'react-chartjs-2';

const Stat = () => {

  ChartJS.register(BarElement, CategoryScale, LinearScale, Title, Tooltip);

  const [allProducts, setAllProducts] = useState([]);

  useEffect(() => {
    fetchDB();
  }, []);

  const fetchDB = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/products', {
        method: 'GET',
      });
      const data = await response.json();
      setAllProducts(data);
    } catch (err) {
      console.error('Failed to fetch products:', err);
    }
  };


  // State to hold chart data
  const [chartData, setChartData] = useState({ labels: [], datasets: [] });

  useEffect(() => {
    const sortedProd = [...allProducts].sort((a, b) => b.sold - a.sold);
    const labels = sortedProd.map(item => item.prod_name);
    const data = sortedProd.map(item => item.sold);

    // Set chart data
    setChartData({
      label: 'sold',
      labels: labels,
      datasets: [{
        data: data,
        backgroundColor: [
          'rgba(255, 99, 132, 0.6)',
          'rgba(54, 162, 235, 0.6)',
          'rgba(255, 206, 86, 0.6)',
          'rgba(75, 192, 192, 0.6)',
          'rgba(153, 102, 255, 0.6)',
          // Add more colors if you have many items
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(153, 102, 255, 1)',
          // Add more colors if you have many items
        ],
        borderWidth: 1,
      }],
    });

  }, [allProducts]);

  const options = {
    indexAxis: 'y',
    elements: {
      bar: {
        borderWidth: 2,
      },
    },
    responsive: true,
    plugins: {
      legend: {
        position: 'right',
      },
      title: {
        display: true,
        text: 'Products Sold (Descending)',
      },
    },
  };

  return (
    <div style={{width: "600px", height: "600px"}}>
      <Bar data={chartData} options={options}/>
    </div>
  );
};

export default Stat;
