import React, { useState } from 'react';
import { Form, Button, Modal } from 'react-bootstrap';
import { getCountry, getCountryByAbbreviation } from 'country-currency-map';
import { getNames, getCode } from 'country-list';
import Payment from '../Components/Payment';
import './CheckoutBox.css';

const CheckoutBox = ({ user, totalPrice, handleCheckout }) => {
  const [show, setShow] = useState(false);
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState(user ? user.email : '');
  const [phone, setPhone] = useState(user ? user.phone : '');
  const [country, setCountry] = useState('');

  const countries = getNames();


  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const convertedCountry = getCountryByAbbreviation(country);
  const currency = getCountry(convertedCountry).currency.toLowerCase();
  const paymentData = {
      amount: Math.round(totalPrice), // Amount to charge in cents
      currency: currency,
      // description : user ? `${user.username} purchased` : 'Guest purchase',
      customer: {
        email: email,
        phone: phone,
        name: `${firstName} ${lastName}`,
      },
    }


  return (
    <>
      <Button variant="primary" className="checkout-button" onClick={handleShow}>
        Checkout
      </Button>

      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header>
          <Modal.Title className="payment-title">Secure Checkout</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="payment-container">
            <Form onSubmit={handleSubmit} className="payment-form">
            <div className="name-field">
                <Modal.Group controlId="formFirstName" className="half-width-input">
                  <Modal.Label>First Name</Modal.Label>
                  <Modal.Control
                    type="text"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    placeholder="Enter your first name"
                    required
                  />
                </Modal.Group>
                <Modal.Group controlId="formLastName" className="half-width-input">
                  <Modal.Label>Last Name</Modal.Label>
                  <Form.Control
                    type="text"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                    placeholder="Enter your last name"
                    required
                  />
                </Modal.Group>
              </div>
              <div className="location-field">
                <Modal.Group controlId="formCountry" className="half-width-input">
                  <Modal.Label>Country</Modal.Label>
                  <Form.Control
                    as="select"
                    value={country}
                    onChange={(e) => setCountry(e.target.value)}
                    required>
                    <option value="">Select a country</option>
                    {countries.map((c, index) => (
                      <option key={index} value={getCode(c)}>
                        {c}
                      </option>
                    ))}
                  </Form.Control>
                </Modal.Group>
              </div>
              <Modal.Group controlId="formEmail">
                <Modal.Label>Email address</Modal.Label>
                <Form.Control
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder={user ? '' : "Enter your email"}
                  required
                />
              </Modal.Group>

              <Modal.Group controlId="formPhone">
                <Modal.Label>Phone Number</Modal.Label>
                <Form.Control
                  type="tel"
                  value={phone}
                  onChange={(e) => setPhone(e.target.value)}
                  pattern="[0-9]{3}[0-9]{3}[0-9]{4}"
                  placeholder={user ? '' : "Enter your phone number"}
                  required
                />
              </Modal.Group>
              <Button variant="primary" type="submit" className="payment-form-button">
                Proceed with payment
              </Button>
            </Form>
          </div>
          <Payment
            handleCheckout={ handleCheckout }
            totalPrice={ parseInt(totalPrice * 100) }
            paymentData={ paymentData }
          />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" className="close-button" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default CheckoutBox;
