import React, { useState, useEffect } from 'react';
import { Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  BarElement } from 'chart.js';
import { Doughnut, Bar } from 'react-chartjs-2';
import { useParams } from 'react-router-dom';

const UserHistory = () => {
  const params = useParams();
  const userId = params.id;

  // State to store the fetched history data
  const [historyData, setHistoryData] = useState(null);

  useEffect(() => {
    // Fetch the history data when the component is mounted
    const fetchHistoryData = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/users`);
        let users = await response.json();
        const targetUser = users.find(user => user._id === userId);
        if (targetUser) {
          setHistoryData(targetUser.bought);
        } else {
          console.error("User not found");
        }
      } catch (error) {
        console.error("Failed to fetch history data:", error);
      }
    };

    fetchHistoryData();
  }, [userId]);


  if (!historyData) {
    return <div>Loading...</div>;
  }

  ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, BarElement);

  const totalQuantities = historyData
    .flat()
    .reduce((acc, item) => {
      Object.keys(item).forEach((key) => {
        if (!acc[key]) {
          acc[key] = item[key];
        } else {
          acc[key] += item[key];
        }
      });
      return acc;
    }, {});

  const backgroundColor = [
    'rgba(255, 99, 132, 0.5)',
    'rgba(53, 162, 235, 0.5)',
    'rgba(181, 2, 160, 0.5)',
    'rgba(227, 223, 0, 0.5)',
    'rgba(34, 139, 34, 0.5)',
  ]
  const borderColor = [
    'rgba(255, 99, 132)',
    'rgba(53, 162, 235)',
    'rgba(181, 2, 160)',
    'rgba(227, 223, 0)',
    'rgba(34, 139, 34)',
  ]

  const doughnutData = {
    labels: Object.keys(totalQuantities),
    datasets: [
      {
        label: '# of purchases',
        data: Object.values(totalQuantities),
        backgroundColor: backgroundColor,
        borderColor: borderColor,
        borderWidth: 1,
      },
    ],
  };

  const barData = {
    labels: historyData.map((_, index) => `Session ${index + 1}`),
    datasets: Object.keys(historyData).map((key, i) => ({
      label: key,
      data: historyData.map((session) => session[key] || 0),
      backgroundColor: backgroundColor[i],
      borderColor: borderColor[i],
      borderWidth: 1,
    })),
  };

  const options = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };


  // Display the history data
  return (
    <div>
      <h1>User ID: {userId}</h1>
      <h2>Total Quantities:</h2>
      <pre>{JSON.stringify(totalQuantities, null, 2)}</pre>
      <Doughnut data={doughnutData} />
      <Bar data={barData} options={options}/>
    </div>
  );
}

export default UserHistory;
