import React, { useEffect, useState } from 'react';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, BarElement, CategoryScale } from 'chart.js';
import { Bar, Pie } from 'react-chartjs-2';

ChartJS.register(ArcElement, Tooltip, Legend, BarElement, CategoryScale);

const Stat = () => {

  const [allProducts, setAllProducts] = useState([]);

  useEffect(() => {
    fetchDB();
  }, []);

  const fetchDB = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/products', {
        method: 'GET',
      });
      let data = await response.json();
      setAllProducts(data.filter(item => item.quantity !== 0));
    } catch (err) {
      console.error('Failed to fetch products:', err);
    }
  };

  const [barChartData, setBarChartData] = useState({ labels: [], datasets: [] });
  const [pieChartData, setPieChartData] = useState({ labels: [], datasets: [] });

  useEffect(() => {
    const sortedProd = [...allProducts].sort((a, b) => b.sold - a.sold);
    const labels = sortedProd.map(item => item.prod_name);
    const data = sortedProd.map(item => item.sold);

    // Set bar chart data
    setBarChartData({
      labels: labels,
      datasets: [{
        label: 'Sold',
        data: data,
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      }],
    });

    // Set pie chart data
    setPieChartData({
      labels: labels,
      datasets: [{
        label: 'Sold',
        data: data,
        backgroundColor: [
          'rgba(255, 99, 132, 0.2)',
          'rgba(54, 162, 235, 0.2)',
          'rgba(255, 206, 86, 0.2)',
          'rgba(75, 192, 192, 0.2)',
          'rgba(153, 102, 255, 0.2)',
          // Add more colors if you have many items
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(153, 102, 255, 1)',
          // Add more colors if you have many items
        ],
        borderWidth: 1,
      }],
    });
  }, [allProducts]);

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      tooltip: {
        callbacks: {
          label: function(context) {
            let total = context.dataset.data.reduce((a, b) => a + b, 0);
            let value = context.raw;
            let percentage = ((value/total) * 100).toFixed(2);
            return context.label + ': ' + percentage + '%';
          }
        }
      }
    }
  }

  return (
    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
      <div style={{width: "600px", height: "600px"}}>
        <Bar data={barChartData} options={options} />
      </div>
      <div style={{width: "600px", height: "600px"}}>
        <Pie data={pieChartData} options={options} />
      </div>
    </div>
  );
};

export default Stat;
