import React, { useState } from 'react';
import { Form, Button, Modal } from 'react-bootstrap';
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';
import './CheckoutBox.css';

const CheckoutBox = ({ handleCheckout }) => {
  const [show, setShow] = useState(false);
  const stripe = useStripe();
  const elements = useElements();

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleSubmit = async (event) => {
    event.preventDefault();
    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: 'card',
      card: elements.getElement(CardElement),
    });

    if (!error) {
      console.log(paymentMethod);
      handleCheckout(); // Call your handleCheckout function here
    }
  };

  return (
    <>
      <Button variant="primary" className="checkout-box-button" onClick={handleShow}>
        Checkout
      </Button>

      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>Secure Checkout</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group className='client-name'>
              <Form.Control type="text" placeholder="First Name" />
            </Form.Group>
            <Form.Group className='client-name'>
              <Form.Control type="text" placeholder="Last Name" />
            </Form.Group>
            <div className="card-element-wrapper">
              <CardElement />
            </div>
            <Button variant="primary" type="submit" block>
              Pay
            </Button>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default CheckoutBox;
