import React, { useState } from 'react';
import { Card, Button } from 'react-bootstrap';
import "./Product.css";

const Product = ({ product }) => {
  const [cartItems, setCartItems] = useState([]);

  const addToCart = () => {
    const existingItem = cartItems.find(item => item.name === product.name);
    if (existingItem) {
      const updatedItem = { ...existingItem, quantity: existingItem.quantity + 1 };
      const updatedItems = cartItems.map(item =>
        item.name === product.name ? updatedItem : item
      );
      setCartItems(updatedItems);
    } else {
      const newItem = { name: product.name, price: product.price, quantity: 1 };
      setCartItems([...cartItems, newItem]);
    }
  };

  return (
    <Card style={{ width: '18rem' }}>
      <Card.Body>
        <Card.Title>{product.name}</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">${product.price}</Card.Subtitle>
        <Card.Text>{product.description}</Card.Text>
        <Button variant="primary" onClick={addToCart}>Add to Cart</Button>
        <Button variant="success" className="ml-2">Buy Now</Button>
        {cartItems.map(item => (
          <div key={item.name}>
            <p>{item.name} - ${item.price} - Quantity: {item.quantity}</p>
          </div>
        ))}
      </Card.Body>
    </Card>
  );
};

export default Product;
