import React from 'react';
import { CartProvider } from './Components/CartContext';
import Cart from './Components/Cart';
import ProductList from './Components/ProductList';
import CartDisplay from './Components/CartDisplay';
import './App.css';

export const fetchProducts = async () => {
  try {
    const response = await fetch('http://localhost:8000/api/products', {
      method: 'GET',
    });
    let data = await response.json();
    data = data.filter((product) => product.quantity !== 0);
    return data;
  } catch (error) {
    console.error('Failed to fetch products:', error);
  }
}

function App() {
  const [products, setProducts] = React.useState([]);
  React.useEffect(() => {
    fetchProductsData();
  }, []);

   const fetchProductsData = async () => {
    try {
      let data = await fetchProducts();
      setProducts(data);
    } catch (error) {
      console.error('Failed to fetch products:', error);
    }
  };

  const removeZeroQuantity = () => {
    setProducts((products) =>
        products.filter((product) => product.quantity !== 0)
    );
  }

  const handleCheckout = async () => {
    if (cart.length > 0) {
      try {
        const updatePromises = [];

        for (const item of cart) {
          console.log(item);
          const { id, quantity } = item;
          const product = products.find((product) => product._id === id);
          if (product) {
            const updatedQuantity = product.quantity - quantity;
            if (updatedQuantity >= 0) {
              const updatePromise = fetch(`http://localhost:8000/api/products/${id}`, {
                method: 'PUT',
                body: JSON.stringify({ prod_name: product.prod_name, description: product.description, price: product.price, quantity: updatedQuantity }),
                headers: {
                  'Content-Type': 'application/json',
                },
              });
              updatePromises.push(updatePromise);
            } else {
              alert(`Insufficient "${product.prod_name}" in stock.`);
            }
          }
        }
        if (updatePromises.length > 0) {
          await Promise.all(updatePromises)
          alert(`Checkout completed. Total price: $${totalPrice}`);
          clearCart();
        }
      } catch (err) {
        alert('An error occurred during checkout. Please try again.');
      }
      removeZeroQuantity();
    } else {
      alert('Your cart is empty.');
    }
  };

  return (
    <CartProvider>
      <div className="App">
        <Cart />
        <ProductList products={ products }/>
        <CartDisplay products={ products }
        handleCheckout= { handleCheckout }/>
      </div>
    </CartProvider>
  );
}

export default App;
