import React, { useContext } from 'react';
import { CartContext } from './CartContext';
import './CartDisplay.css';

const CartDisplay = () => {
  const { cart, isCartVisible, toggleCartVisibility, clearCart, addToCart } = useContext(CartContext);

  if (!isCartVisible) {
    return null;
  }

  const handleClearCart = () => {
    clearCart();
  };

  const handleCloseCart = () => {
    toggleCartVisibility();
  };

  const handleCheckout = () => {
    if (cart.length > 0) {
      const totalPrice = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);
      alert(`Checkout completed. Total price: $${totalPrice.toFixed(2)}`);
      clearCart();
    } else {
      alert('Your cart is empty.');
    }
  };

  const getCartItem = (item) => {
    const existingItem = cart.find((cartItem) => cartItem.name === item.name);
    if (existingItem) {
      return { ...existingItem, quantity: existingItem.quantity + 1 };
    }
    return { ...item, quantity: 1 };
  };

  const handleAddToCart = (item) => {
    const cartItem = getCartItem(item);
    addToCart(cartItem);
  };

  const handleRemoveFromCart = (item) => {
    const existingItem = cart.find((cartItem) => cartItem.name === item.name);
    if (existingItem) {
      if (existingItem.quantity > 1) {
        const updatedItem = { ...existingItem, quantity: existingItem.quantity - 1 };
        addToCart(updatedItem);
      } else {
        const filteredCart = cart.filter((cartItem) => cartItem.name !== item.name);
        clearCart();
        filteredCart.forEach((cartItem) => addToCart(cartItem));
      }
    }
  };

  const totalPrice = cart.reduce((acc, item) => acc + (parseFloat(item.price) * item.quantity), 0).toFixed(2);

  return (
    <div className="cart-display">
      <h2>Shopping Cart</h2>
      <div className="total-price">
        <p>Total Price:</p>
        <p>${totalPrice}</p>
      </div>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          {cart.map((item, index) => (
            <div key={index} className="cart-item">
              <p>
                {item.name} <span className="item-quantity">{item.quantity}</span>
              </p>
              <p>${(parseFloat(item.price) * item.quantity).toFixed(2)}</p>
              <div className="item-buttons">
                <button className="remove-from-cart-button" onClick={() => handleRemoveFromCart(item)}>
                  -
                </button>
                <button className="add-to-cart-button" onClick={() => handleAddToCart(item)}>
                  +
                </button>
              </div>
            </div>
          ))}
          <div className="cart-buttons">
            <button className="clear-cart-button" onClick={handleClearCart}>
              Clear Cart
            </button>
            <button className="checkout-button" onClick={handleCheckout}>
              Checkout
            </button>
            <button className="close-cart-button" onClick={handleCloseCart}>
              Close Cart
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default CartDisplay;
