import React, { useEffect, useState } from 'react';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';

const Stat = ({ products, fetchProducts }) => {

  ChartJS.register(ArcElement, Tooltip, Legend);
  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  // State to hold chart data
  const [chartData, setChartData] = useState({ labels: [], datasets: [] });

  useEffect(() => {
    const labels = products.map(item => item.prod_name);
    const data = products.map(item => item.sold);

    // Set chart data
    setChartData({
      labels: labels,
      datasets: [{
        data: data,
        backgroundColor: [
          'rgba(255, 99, 132, 0.2)',
          'rgba(54, 162, 235, 0.2)',
          'rgba(255, 206, 86, 0.2)',
          'rgba(75, 192, 192, 0.2)',
          'rgba(153, 102, 255, 0.2)',
          // Add more colors if you have many items
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(153, 102, 255, 1)',
          // Add more colors if you have many items
        ],
        borderWidth: 1,
      }],
    });

  }, [products]);

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      tooltip: {
        callbacks: {
          label: function(context) {
            let total = context.dataset.data.reduce((a, b) => a + b, 0);
            let value = context.raw;
            let percentage = ((value/total) * 100).toFixed(2);
            return context.label + ': ' + percentage + '%';
          }
        }
      }
    }
  }

  return (
    <div style={{width: "600px", height: "600px"}}>
      <Pie data={chartData} options={options} key={Math.random()}/>
    </div>
  );
};

export default Stat;
