import React, { useEffect, useState } from 'react';
import { Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
  BarElement,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement, } from 'chart.js';
import { Bar, Pie, Line } from 'react-chartjs-2';

ChartJS.register(ArcElement, Tooltip, Legend, BarElement, CategoryScale, LinearScale, PointElement, LineElement);

const Stat = () => {

  const [allProducts, setAllProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetchDB();
  }, []);

  const fetchDB = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:8000/api/products', {
        method: 'GET',
      });
      let data = await response.json();
      const siftedData = data.filter(item => item.quantity !== 0);
      if (siftedData.length === 0) {
        setAllProducts(data);
      } else {
        setAllProducts(siftedData);
      }
      setIsLoading(false);
    } catch (err) {
      console.error('Failed to fetch products:', err);
      setIsLoading(false);
    }
  };

  const [barChartData, setBarChartData] = useState({ labels: [], datasets: [] });
  const [pieChartData, setPieChartData] = useState({ labels: [], datasets: [] });
  const [lineChartData, setLineChartData] = useState({ labels: [], datasets: [] });

  useEffect(() => {
    if (!isLoading) {
      const sortedProd = [...allProducts].sort((a, b) => b.sold - a.sold);
      const labels = sortedProd.map(item => item.prod_name);
      const data = sortedProd.map(item => item.sold);

      // Set bar chart data
      setBarChartData({
        labels: labels,
        datasets: [{
          label: 'Sold',
          data: data,
          backgroundColor: 'rgba(75, 192, 192, 0.2)',
          borderColor: 'rgba(75, 192, 192, 1)',
          borderWidth: 1,
        }],
      });

      const backgroundColor = [
        'rgba(220, 20, 60, 0.2)',
        'rgba(255, 255, 0, 0.2)',
        'rgba(255, 218, 185, 0.2)',
        'rgba(240, 230, 140, 0.2)',
        'rgba(34, 139, 34, 0.2)',
      ]
      const borderColor = [
        'rgba(220, 20, 60, 1)',
        'rgba(255, 255, 0, 1)',
        'rgba(255, 218, 185, 1)',
        'rgba(240, 230, 140, 1)',
        'rgba(34, 139, 34, 1)',
      ]
      // Set pie chart data
      setPieChartData({
        labels: labels,
        datasets: [{
          label: 'Sold',
          data: data,
          backgroundColor: backgroundColor,
          borderColor: borderColor,
          borderWidth: 1,
        }],
      });

      // Set line chart data
      const lineData = sortedProd.map(item => item.record);
      const numbers = Array.from({ length: 50 }, (_, index) => index + 1);
      setLineChartData({
        labels: numbers,
        datasets: [
          {
            label: labels[0],
            data: lineData[0],
            backgroundColor: 'rgba(75, 192, 192, 0.2)',
            borderColor: 'rgba(75, 192, 192, 1)',
            borderWidth: 1,
          },
          {
            label: labels[1],
            data: lineData[1],
            backgroundColor: 'rgba(75, 192, 192, 0.2)',
            borderColor: 'rgba(75, 192, 192, 1)',
            borderWidth: 1,
          },
          {
            label: labels[2],
            data: lineData[2],
            backgroundColor: 'rgba(75, 192, 192, 0.2)',
            borderColor: 'rgba(75, 192, 192, 1)',
            borderWidth: 1,
          },
          {
            label: labels[3],
            data: lineData[3],
            backgroundColor: 'rgba(75, 192, 192, 0.2)',
            borderColor: 'rgba(75, 192, 192, 1)',
            borderWidth: 1,
          },
          {
            label: labels[4],
            data: lineData[4],
            backgroundColor: 'rgba(75, 192, 192, 0.2)',
            borderColor: 'rgba(75, 192, 192, 1)',
            borderWidth: 1,
          }
        ],
      });
    }
  }, [allProducts, isLoading]);

  const optionsForBar = {
    responsive: true,
    maintainAspectRatio: false,
    indexAxis: 'x',
    plugins: {
      tooltip: {
        callbacks: {
          label: function(context) {
            let value = context.raw;
            return context.label + ' sold: ' + value;
          }
        }
      }
    }
  };

  const optionsForPie = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      tooltip: {
        callbacks: {
          label: function(context) {
            let total = context.dataset.data.reduce((a, b) => a + b, 0);
            let value = context.raw;
            let percentage = ((value/total) * 100).toFixed(2);
            return context.label + ': ' + percentage + '%';
          }
        }
      }
    }
  }

  return (
    <div className='charts' style={{ display: 'flex', justifyContent: 'space-between' }}>
      <div style={{width: "600px", height: "600px"}}>
        <Bar data={barChartData} options={optionsForBar}/>
      </div>
      <div style={{width: "600px", height: "600px"}}>
        <Pie data={pieChartData} options={optionsForPie}/>
      </div>
      <div style={{width: "600px", height: "600px"}}>
        <Line data={lineChartData} />
      </div>
    </div>
  );
};

export default Stat;
