import React, { useContext } from 'react';
import { Navbar, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { CartContext } from './CartContext';
import './Navbar.css';

const NavBar = () => {
  const { cart, toggleCartVisibility } = useContext(CartContext);
  const navigate = useNavigate();

  const getTotalSum = () => {
    let sum = 0;
    cart.forEach((item) => {
      sum += item.quantity;
    });
    return sum;
  };

  return (
    <Navbar bg="light" expand="lg" className="justify-content-between">
      <Navbar.Brand href="/">Shopping Site</Navbar.Brand>
      <div>
        <Button className="mr-2" variant="outline-primary" onClick={() => navigate('/login')}>
          Admin
        </Button>
        <Button variant="outline-success" onClick={toggleCartVisibility}>
          Cart ({getTotalSum()})
        </Button>
      </div>
    </Navbar>
  );
};

export default NavBar;
