import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';

const Stat = () => {
  const [productStats, setProductStats] = useState([]);
  const [sortedProducts, setSortedProducts] = useState([]);

  useEffect(() => {
    fetchProductStats();
  }, []);

  const fetchProductStats = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/productStats');
      const data = await response.json();
      setProductStats(data);
      sortProductsByPopularity(data);
    } catch (error) {
      console.error('Failed to fetch product stats:', error);
    }
  };

  const sortProductsByPopularity = (stats) => {
    // Sort the products based on their popularity
    const sorted = [...stats].sort((a, b) => b.popularity - a.popularity);
    setSortedProducts(sorted);
  };

  const handleCheckout = () => {
    // Handle the checkout process and update the statistics accordingly
    // Make the necessary API calls to update the product stats on the server

    // Fetch the updated product stats
    fetchProductStats();
  };

  // Prepare data for the chart
  const chartData = {
    labels: sortedProducts.map((product) => product.name),
    datasets: [
      {
        label: 'Popularity',
        data: sortedProducts.map((product) => product.popularity),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  };

  // Chart options
  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      y: {
        beginAtZero: true,
        max: 100, // Adjust as needed
      },
    },
  };

  return (
    <div>
      <h2>Product Statistics</h2>
      <div>
        <h3>Popularity Chart</h3>
        <Bar data={chartData} options={chartOptions} />
      </div>
      <div>
        <h3>Sorted Products by Popularity</h3>
        <ul>
          {sortedProducts.map((product) => (
            <li key={product.id}>
              {product.name} - Popularity: {product.popularity}
            </li>
          ))}
        </ul>
      </div>
      <button onClick={handleCheckout}>Checkout</button>
    </div>
  );
};

export default Stat;
