import React, { useEffect, useState } from 'react';
import { Navbar, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { CartContext } from './CartContext';
import './Navbar.css';

const NavBar = () => {
  const [scrolled, setScrolled] = useState(false);
  const { cart, toggleCartVisibility } = useContext(CartContext);
  const navigate = useNavigate();

  const getTotalSum = () => {
    let sum = 0;
    cart.forEach((item) => {
      sum += item.quantity;
    });
    return sum;
  };

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.pageYOffset;
      if (scrollTop > 100) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <Navbar expand="lg" className={`navbar navbar-dark ${scrolled ? 'scrolled' : ''}`}>
      <Navbar.Brand href="/" className="brand">My Shopping Site</Navbar.Brand>
      <div>
        <Button className="admin-button" onClick={() => navigate('/login')}>
          Admin
        </Button>
        <Button className="cart-button" onClick={toggleCartVisibility}>
          Cart ({getTotalSum()})
        </Button>
      </div>
    </Navbar>
  );
};

export default NavBar;
