import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button } from 'react-bootstrap';

const Admin = () => {
  const navigate = useNavigate();
  const [allProducts, setAllProducts] = useState([]);
  const [deletedItemId, setDeletedItemId] = useState(null);

  useEffect(() => {
    fetchDB();
  }, []);

  const fetchDB = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/products', {
        method: 'GET',
      });
      const data = await response.json();
      setAllProducts(data);
    } catch (error) {
      console.error('Failed to fetch products:', error);
    }
  };

  const handleDelete = (itemId) => {
    setDeletedItemId(itemId);
  };

  useEffect(() => {
    if (deletedItemId) {
      deleteProduct(deletedItemId);
    }
  }, [deletedItemId]);

  const deleteProduct = async (itemId) => {
    try {
      await fetch(`http://localhost:8000/api/products/${itemId}`, {
        method: 'DELETE',
      });
      setAllProducts((prevProducts) =>
        prevProducts.filter((product) => product._id !== itemId)
      );
    } catch (error) {
      console.error('Failed to delete product:', error);
    } finally {
      setDeletedItemId(null);
    }
  };

  return (
    <div className="admin-container">
      <h2 className="admin-heading">Product List</h2>
      <table className="product-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Product Name</th>
            <th>Description</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {allProducts.map((product) => (
            <tr key={product._id}>
              <td>{product._id}</td>
              <td>{product.prod_name}</td>
              <td>{product.description}</td>
              <td>{product.price}</td>
              <td>{product.quantity}</td>
              <td>
                <Button
                  className="action-button remove"
                  onClick={() => handleDelete(product._id)}
                >
                  Remove Stock
                </Button>
                <Button className="action-button add">Add Stock</Button>
                <Button className="action-button delete">Delete Item</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Button className="add-item" onClick={() => navigate('/')}>
        Add item
      </Button>
      <Button className="back-button" onClick={() => navigate('/')}>
        Log off
      </Button>
    </div>
  );
};

export default Admin;
