import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Button } from 'react-bootstrap';
import './Admin.css';

const Admin = () => {
  const navigate = useNavigate();
  const [allProducts, setAllProducts] = React.useState([]);
  const [deleteItemId, setDeleteItemId] = React.useState([]);

  React.useEffect(() => {
    fetchDB();
  }, []);

  const fetchDB = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/products', {
        method: 'GET',
      });
      const data = await response.json();
      setAllProducts(data);
    } catch (error) {
      console.error('Failed to fetch products:', error);
    }
  };

  const handleDelete = (itemId) => {
    setDeleteItemId(itemId);
  }

  React.useEffect(() => {
    if (deleteItemId) {
      deleteProduct(deleteItemId);
    }
  }, [deleteItemId]);

  const deleteProduct = async (itemId) => {
    try {
      await fetch(`http://localhost:8000/api/products/${itemId}`, {
        method: 'DELETE',
      });
      setAllProducts((products) =>
        products.filter((product) => product._id !== itemId)
      );
    } catch (error) {
      console.error('Failed to delete product:', error);
    } finally {
      setDeleteItemId(null);
    }
  };

  return (
    <div className="admin-container">
      <h2 className="admin-heading">Product List</h2>
      <table className="product-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Product Name</th>
            <th>Description</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {allProducts.map((product) => (
            <tr key={product._id}>
              <td>{product._id}</td>
              <td>{product.prod_name}</td>
              <td>{product.description}</td>
              <td>${product.price}</td>
              <td>{product.quantity}</td>
              <td>
                <Button className="action-button remove">Remove Stock</Button>
                <Button className="action-button add">Add Stock</Button>
                <Button className="action-button delete" onClick={() => handleDelete(product._id)}>Delete Item</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Button className="add-item" onClick={() => navigate('/')}>
        Add Item
      </Button>
      <Button className="back-button" onClick={() => navigate('/')}>
        Log Off
      </Button>
    </div>
  );
};

export default Admin;
