import React, { useContext } from 'react';
import { Button } from 'react-bootstrap';
import { CartContext } from './CartContext';
import './Header.css';

const Cart = () => {
  const { cart } = useContext(CartContext);

  return (
    <header>
      <Button className="cart-button">Cart ({cart.length})</Button>
    </header>
  );
};

export default Cart;
