import React from 'react';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Doughnut } from 'react-chartjs-2';
import { useLocation, useParams } from 'react-router-dom';

function UserHistory() {
  const location = useLocation();
  const params = useParams();

  // Get the user id from route params
  const userId = params.id;

  // Get the history data from state
  const historyData = location.state?.history;

  // Handle case where history data or id was not passed correctly
  if (!userId || !historyData) {
    return (
      <div>
        <h1>Error</h1>
        <p>Invalid user ID or history data.</p>
      </div>
    );
  }

  ChartJS.register(ArcElement, Tooltip, Legend);

  const totalQuantities = historyData
    .flat()
    .reduce((acc, item) => {
      Object.keys(item).forEach((key) => {
        if (!acc[key]) {
          acc[key] = item[key];
        } else {
          acc[key] += item[key];
        }
      });
      return acc;
    }, {});

  const backgroundColor = [
    'rgba(255, 99, 132, 0.5)',
    'rgba(53, 162, 235, 0.5)',
    'rgba(181, 2, 160, 0.5)',
    'rgba(227, 223, 0, 0.5)',
    'rgba(34, 139, 34, 0.5)',
  ]
  const borderColor = [
    'rgba(255, 99, 132)',
    'rgba(53, 162, 235)',
    'rgba(181, 2, 160)',
    'rgba(227, 223, 0)',
    'rgba(34, 139, 34)',
  ]

  const chartData = {
    labels: Object.keys(totalQuantities),
    datasets: [
      {
        label: '# of purchases',
        data: Object.values(totalQuantities),
        backgroundColor: backgroundColor,
        borderColor: borderColor,
        borderWidth: 1,
      },
    ],
  };

  // Display the history data
  return (
    <div>
      <h1>User ID: {userId}</h1>
      <h2>Total Quantities:</h2>
      <pre>{JSON.stringify(totalQuantities, null, 2)}</pre>
      <Doughnut data={chartData} />
    </div>
  );
}

export default UserHistory;
